import { Component, Input } from '@angular/core';
import { GiftModel } from 'src/app/models/gift-model';
import { DataService } from 'src/app/services/data.service';

@Component({
  selector: 'app-gift-card',
  templateUrl: './gift-card.component.html',
  styleUrls: ['./gift-card.component.css']
})
export class GiftCardComponent {

  @Input()
  public gift: GiftModel

  public gifts: GiftModel[]=[];

  public constructor(private dataService: DataService) {  }

  public async deleteMe(_id: string) {
    try{
      if(!window.confirm("Are you sure?")) return;

      await this.dataService.deleteGift(_id);
      alert("Gift has been deleted!")

      const index = this.gifts.findIndex(g => g._id === _id);
      this.gifts.splice(index , )
    }
    catch(err: any){

    }
  }
}

<div class="card">
    <div class="container">
      <h4><b>{{gift.giftName}}</b></h4>
      <p>{{gift.description}}</p>
      <p>{{gift.price}}</p>
      <p>{{gift.discount}}</p>
      <button (click)="deleteMe(gift._id)" >❌</button>
    </div>
  </div>
