import { Component } from '@angular/core';
import { Router } from '@angular/router';
import { GiftModel } from 'src/app/models/gift-model';
import { TargetModel } from 'src/app/models/taget-model';
import { DataService } from 'src/app/services/data.service';

@Component({
  selector: 'app-add',
  templateUrl: './add.component.html',
  styleUrls: ['./add.component.css']
})
export class AddComponent {
  public targets:TargetModel[] = [];
  public gift = new GiftModel();

  public constructor(private dataService: DataService, private router: Router) { }

  public  async ngOnInit() {
    try{
           this.targets = await this.dataService.getAllTargets();
    }
    catch(err:any){
      alert(err.message)
    }
  }

  public async send(){
    try{
       await this.dataService.addGift(this.gift)
       alert ("Gift has been added")
       alert(this.gift)
       this.router.navigateByUrl("/list")
    }
    catch(err:any){

  }
}
}
<p>Add a Gift</p>

<div class="f">
    <form (ngSubmit)="send()" ngNativeValidate>

        <label for="country">Target - Audience</label>
      <select name="targetId" [(ngModel)]="gift.targetId" required>
        <option selected disabled value="">Select Gift</option>
        <option *ngFor="let t of targets" value={{t._id}}>{{t.type}}</option>
    </select>

      <label for="fname">Gift Name</label>
      <input type="text" id="fname" name="firstname" placeholder="Gift name.." [(ngModel)]="gift.giftName" required>

      <label for="lname">description</label>
      <input type="text" id="lname" name="lastname" placeholder="Description.." [(ngModel)]="gift.description" required >

      <label for="lname">price</label>
      <input type="text" id="lname" name="lastname" placeholder="Price.."  [(ngModel)]="gift.price" required>


      <label for="lname">discount</label>
      <input type="text" id="lname" name="lastname" placeholder="Discount.."  [(ngModel)]="gift.discount" required>


      <input type="submit" value="Submit">
    </form>
  </div>
